kibanaSavedObjectMeta.searchSourceJSON:
{
  "index": "*user*latest*",
  "query": {
    "query_string": {
      "query": "*",
      "analyze_wildcard": true
    }
  },
  "filter": [
    {
      "meta": {
        "index": "*user*latest*",
        "field": "scripted_usr_account_status",
        "key": "scripted_usr_account_status",
        "value": "active-6m",
        "disabled": false,
        "negate": false,
        "alias": null,
        "apply": true
      },
      "script": {
        "script": {
          "inline": "boolean compare(Supplier s, def v) {return s.get() == v;}compare(() -> { if (doc['usr_last_login_date'].value - doc['usr_created_date'].value == 0.0){\n        return \"used once\";\n} \nelse if (doc['usr_last_login_date'].value - doc['usr_created_date'].value < 0.0) {\n        return \"not activated\";\n} \nelse if (doc['usr_last_login_date'].value - doc['usr_created_date'].value < 15770000000.0){\n    return \"active-6m\";\n}\nelse {\n    return \"inactive\";\n} }, params.value);",
          "lang": "painless",
          "params": {
            "value": "active-6m"
          }
        }
      },
      "$state": {
        "store": "appState"
      }
    }
  ]
}
